type counter struct{}

func (h counter) mount(r chi.Router, db zdb.DB) {
	// This relies on e.g. Varnish for more extended caching.
	c := r.With(middleware.Compress(2), func(next http.Handler) http.Handler {
		return http.HandlerFunc(func(w http.ResponseWriter, r *http.Request) {
			w.Header().Set("Cache-Control", "public")
			w.Header().Set("Expires", goatcounter.Now().Add(30*time.Minute).Format(time.RFC1123Z))
			next.ServeHTTP(w, r)
		})
	})

	c.Get("/counter/*", zhttp.Wrap(h.counter))
}

var (
	fontFaceOnce sync.Once
	fontFace     font.Face
	fontColor    *image.Uniform
)

func (h counter) counter(w http.ResponseWriter, r *http.Request) error {
	path := r.URL.Path[9:]
	ext := filepath.Ext(path)
	path = path[:len(path)-len(ext)]

	var hs goatcounter.HitStats
	err := hs.ListPathsLike(r.Context(), path, false)
	if err != nil {
		return err
	}
	if len(hs) == 0 {
		hs = goatcounter.HitStats{{}}
	}

	count := tplfunc.Number(hs[0].CountUnique, Site(r.Context()).Settings.NumberFormat)

	switch ext {
	default:
		return guru.Errorf(400, "unknown extension: %q", ext)
	case ".json":
		return zhttp.JSON(w, map[string]string{
			"count_unique": count,
		})
	case ".html":
		w.Header().Set("Content-Type", "text/html")
		return zhttp.String(w, fmt.Sprintf(html, count))
	case ".svg":
		w.Header().Set("Content-Type", "image/svg+xml")
		return zhttp.String(w, fmt.Sprintf(svg, count))
	case ".png":
		fontFaceOnce.Do(func() {
			f, err := opentype.Parse(gobold.TTF)
			if err != nil {
				panic(err)
			}
			fontFace, err = opentype.NewFace(f, &opentype.FaceOptions{
				Size:    22,
				DPI:     72,
				Hinting: font.HintingFull,
			})
			if err != nil {
				panic(err)
			}
			fontColor = image.NewUniform(color.RGBA{R: 0x9a, G: 0x15, B: 0xa4, A: 255})
		})

		bounds := pngImg.Bounds()
		tmp := image.NewRGBA(bounds)
		drw := font.Drawer{
			Dst:  tmp,
			Src:  fontColor,
			Face: fontFace,
			Dot:  fixed.P(0, 22),
		}
		drw.DrawString(count)

		img := image.NewRGBA(bounds)
		for y := bounds.Min.Y; y < bounds.Max.Y; y++ {
			for x := bounds.Min.X; x < bounds.Max.X; x++ {
				img.Set(x, y, pngImg.At(x, y))
			}
		}

		width := drw.Dot.X.Round()
		bounds = tmp.Bounds()
		for y := bounds.Min.Y; y < bounds.Max.Y; y++ {
			for x := bounds.Min.X; x < bounds.Max.X; x++ {
				c := tmp.At(x, y)
				if _, _, _, a := c.RGBA(); a != 0 {
					img.Set(x+width, y+24, c)
				}
			}
		}

		w.Header().Set("Content-Type", "image/png")
		return png.Encode(w, img)
	}
}

